export to "${workingDir}/../csvs/smr2CmrPrdct.csv" of del messages "${workingDir}/../logs/smr2CmrPrdct_db2.log"SELECT  --sctid.newguid, ac.MPP_NUM AS MPP_NUM,  -- account not mpp_num   sme.accounts sme.accounts_cstm CASE    WHEN product_id IS NULL THEN '_ALL_'    ELSE UP.PRODUCT_ID  END Product_ID,  U.EMPLOYEE_CNUM AS User_CNUM,  --CURRENT TIMESTAMP AS intime, CASE    WHEN LENGTH(      TRIM( Ac.landed_country )    )= 0 THEN 'WW'    ELSE Ac.landed_country  END AS Country,  --* CASE    WHEN UP.PRODUCT_LEVEL IS NULL THEN 0    ELSE UP.PRODUCT_LEVEL  END PRODUCT_LEVELFROM  SCTID.IBM_NODE_USERS AS NU --user nodeINNER JOIN SCTID.USERS U ON  NU.ASSIGNED_USER_ID = U.ID  AND u.STATUS = 'Active'INNER JOIN SCTID.ACCOUNTS_USERS AU ON  --user client team  accountid mutil user U.ID = AU.USER_IDINNER JOIN sme.ACCOUNTS A ON  AU.ACCOUNT_ID = A.ID -- AND A.ccms_level = 'S' --只取site的值 加过滤LEFT JOIN SCTID.IBM_USERS_PRODUCTS UP ON  --user sell product U.ID = up.USER_ID  AND UP.DELETED = 0LEFT JOIN SCTID.accounts_cstm AS ac ON  ac.id_c = a.idWHERE  (    NU.USER_TYPE = 'OWNER' -- user 有无 node    OR NU.USER_TYPE = 'ROOWNER' --user type : readonly owner  )  AND NU.DELETED = 0  AND U.DELETED = 0  AND AU.DELETED = 0  AND A.DELETED = 0 --AND UP.DELETED = 0  --AND U.USER_JOB_ROLE_SKILL_SET = 'Security Services' --250人需要确认  AND LENGTH(    TRIM( ac.MPP_NUM )  )<> 0GROUP BY  ac.MPP_NUM,  UP.PRODUCT_ID,  U.EMPLOYEE_CNUM,  Ac.landed_country,  UP.PRODUCT_LEVEL;